import { Component } from "react";
import {MainContainer,LoginCard,ChangeButton,LogoImage,InputField,Labellements} from  './styledComponent.js'
import ThemeContext from "../../context/ThemeContext.js";

class Login extends Component {
  state={username:"",password:""}  

  handle

  render() {
    return (
      <ThemeContext.Consumer>
      {
       value=>{
         const {blacktheme,changeTheme}=value ;
         const onClickChangetheme=()=>{
          changeTheme();
         } 
         return (
           <MainContainer isBlack={blacktheme}>
             <LoginCard isBlack={blacktheme}>
              <div className="flex justify-center w-full">
              {blacktheme===true ?(<LogoImage src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png" alt="DarkTheme"/>)
               :(<LogoImage src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png" alt="lightTheme"/>)}
              </div>
               <Labellements  htmlFor="username" isBlack={blacktheme}>USERNAME</Labellements>
              <InputField id="username" type="input" placeholder="Enter username" value={username}   />
               <Labellements  htmlFor="password" isBlack={blacktheme}>PASSWORD</Labellements>
              <InputField id="username" type="text" placeholder="Enter username" value={username}  />
             </LoginCard>
             <ChangeButton onClick={onClickChangetheme}>Change theme</ChangeButton>
           </MainContainer>
         );
       }
      }
     </ThemeContext.Consumer>
    )

  }
  }
  export default Login
