import { Component } from "react";
import {MainContainer,LoginCard,ChangeButton} from  './styledComponent.js'
import ThemeContext from "../../context/ThemeContext.js";

class Login extends Component {
  render() {
    return (
      <ThemeContext.Consumer>
      {
       value=>{
         const {blacktheme,changeTheme}=value ;
         const onClickChangetheme=()=>{
          changeTheme();
         } 
         return (
           <MainContainer isBlack={blacktheme}>
             <LoginCard isBlack={blacktheme}>
               {blacktheme===true ?(<img src="" alt=""/>):(<img src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png" alt="lightT"/>)}
             </LoginCard>
             <ChangeButton onClick={onClickChangetheme}>Change theme</ChangeButton>
           </MainContainer>
         );
       }
      }
     </ThemeContext.Consumer>
    )

  }
  }
  export default Login
